/**
 * Implements hook_node_update_index().
 *
 * Act on a node being indexed for searching.
 *
 * This hook is invoked during search indexing, after node_load(), and after
 * the result of node_view() is added as $node->rendered to the node object.
 *
 * @param $node
 *   The node being indexed.
 *
 * @return
 *   Array of additional information to be indexed.
 *
 * @ingroup node_api_hooks
 *
 * $COMMENT$
 */
function $MODULE_NAME$_node_update_index($node) {
  $text = '';
  $comments = db_query('SELECT subject, comment, format FROM {comment} WHERE nid = :nid AND status = :status', array(':nid' => $node->nid, ':status' => COMMENT_PUBLISHED));
  foreach ($comments as $comment) {
    $text .= '<h2>' . check_plain($comment->subject) . '</h2>' . check_markup($comment->comment, $comment->format, '', TRUE);
  }
  return $text;
  $END$
}

/**
 * Implements hook_node_update_index().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_node_update_index($node) {
  $text = '';
  $comments = db_query('SELECT subject, comment, format FROM {comment} WHERE nid = :nid AND status = :status', array(':nid' => $node->nid, ':status' => COMMENT_PUBLISHED));
  foreach ($comments as $comment) {
    $text .= '<h2>' . check_plain($comment->subject) . '</h2>' . check_markup($comment->comment, $comment->format, '', TRUE);
  }
  return $text;
  $END$
}

/**
 * Implements hook_node_validate().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_node_validate($node, $form, &$form_state) {
  if (isset($node->end) && isset($node->start)) {
    if ($node->start > $node->end) {
      form_set_error('time', t('An event may not end before it starts.'));
    }
  }
  $END$
}

/**
 * Implements hook_node_submit().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_node_submit($node, $form, &$form_state) {
  // Decompose the selected menu parent option into 'menu_name' and 'plid', if
  // the form used the default parent selection widget.
  if (!empty($form_state['values']['menu']['parent'])) {
    list($node->menu['menu_name'], $node->menu['plid']) = explode(':', $form_state['values']['menu']['parent']);
  }
  $END$
}

