/**
 * Implements hook_query_TAG_alter().
 *
 * Perform alterations to a structured query for a given tag.
 *
 * @param $query
 *   An Query object describing the composite parts of a SQL query.
 *
 * @see hook_query_alter()
 * @see node_query_node_access_alter()
 * @see QueryAlterableInterface
 * @see SelectQueryInterface
 *
 * $COMMENT$
 */
function $MODULE_NAME$_query_TAG_alter(QueryAlterableInterface $query) {
  // Skip the extra expensive alterations if site has no node access control modules.
  if (!node_access_view_all_nodes()) {
    // Prevent duplicates records.
    $query->distinct();
    // The recognized operations are 'view', 'update', 'delete'.
    if (!$op = $query->getMetaData('op')) {
      $op = 'view';
    }
    // Skip the extra joins and conditions for node admins.
    if (!user_access('bypass node access')) {
      // The node_access table has the access grants for any given node.
      $access_alias = $query->join('node_access', 'na', '%alias.nid = n.nid');
      $or = db_or();
      // If any grant exists for the specified user, then user has access to the node for the specified operation.
      foreach (node_access_grants($op, $query->getMetaData('account')) as $realm => $gids) {
        foreach ($gids as $gid) {
          $or->condition(db_and()
            ->condition($access_alias . '.gid', $gid)
            ->condition($access_alias . '.realm', $realm)
          );
        }
      }

      if (count($or->conditions())) {
        $query->condition($or);
      }

      $query->condition($access_alias . 'grant_' . $op, 1, '>=');
    }
  }
  $END$
}

/**
 * Implements hook_query_TAG_alter().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_query_TAG_alter(QueryAlterableInterface $query) {
  // Skip the extra expensive alterations if site has no node access control modules.
  if (!node_access_view_all_nodes()) {
    // Prevent duplicates records.
    $query->distinct();
    // The recognized operations are 'view', 'update', 'delete'.
    if (!$op = $query->getMetaData('op')) {
      $op = 'view';
    }
    // Skip the extra joins and conditions for node admins.
    if (!user_access('bypass node access')) {
      // The node_access table has the access grants for any given node.
      $access_alias = $query->join('node_access', 'na', '%alias.nid = n.nid');
      $or = db_or();
      // If any grant exists for the specified user, then user has access to the node for the specified operation.
      foreach (node_access_grants($op, $query->getMetaData('account')) as $realm => $gids) {
        foreach ($gids as $gid) {
          $or->condition(db_and()
            ->condition($access_alias . '.gid', $gid)
            ->condition($access_alias . '.realm', $realm)
          );
        }
      }

      if (count($or->conditions())) {
        $query->condition($or);
      }

      $query->condition($access_alias . 'grant_' . $op, 1, '>=');
    }
  }
  $END$
}

/**
 * Implements hook_install().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_install() {
  // Populate the default {node_access} record.
  db_insert('node_access')
    ->fields(array(
      'nid' => 0,
      'gid' => 0,
      'realm' => 'all',
      'grant_view' => 1,
      'grant_update' => 0,
      'grant_delete' => 0,
    ))
    ->execute();
  $END$
}

/** * Implements hook_update_N(). * * Perform a single update. * * For each patch which requires a database change add a new hook_update_N() * which will be called by update.php. The database updates are numbered * sequentially according to the version of Drupal you are compatible with. * * Schema updates should adhere to the Schema API: * @link http://drupal.org/node/150215 http://drupal.org/node/150215 @endlink * * Database updates consist of 3 parts: * - 1 digit for Drupal core compatibility * - 1 digit for your module's major release version (e.g. is this the 5.x-1.* (1) or 5.x-2.* (2) series of your module?) * - 2 digits for sequential counting starting with 00 * * The 2nd digit should be 0 for initial porting of your module to a new Drupal * core API. * * Examples: * - mymodule_update_5200() * - This is the first update to get the database ready to run mymodule 5.x-2.*. * - mymodule_update_6000() * - This is the required update for mymodule to run with Drupal core API 6.x. * - mymodule_update_6100() * - This is the first update to get the database ready to run mymodule 6.x-1.*. * - mymodule_update_6200() * - This is the first update to get the database ready to run mymodule 6.x-2.*. * Users can directly update from 5.x-2.* to 6.x-2.* and they get all 60XX * and 62XX updates, but not 61XX updates, because those reside in the * 6.x-1.x branch only. * * A good rule of thumb is to remove updates older than two major releases of * Drupal. See hook_update_last_removed() to notify Drupal about the removals. * * Never renumber update functions. * * Further information about releases and release numbers: * - @link http://drupal.org/handbook/version-info http://drupal.org/handbook/version-info @endlink * - @link http://drupal.org/node/93999 http://drupal.org/node/93999 @endlink (Overview of contributions branches and tags) * - @link http://drupal.org/handbook/cvs/releases http://drupal.org/handbook/cvs/releases @endlink * * Implementations of this hook should be placed in a mymodule.install file in * the same directory as mymodule.module. Drupal core's updates are implemented * using the system module as a name and stored in database/updates.inc. * * If your update task is potentially time-consuming, you'll need to implement a * multipass update to avoid PHP timeouts. Multipass updates use the $sandbox * parameter provided by the batch API (normally, $context['sandbox']) to store * information between successive calls, and the $sandbox['#finished'] value * to provide feedback regarding completion level. * * See the batch operations page for more information on how to use the batch API: * @link http://drupal.org/node/180528 http://drupal.org/node/180528 @endlink * * @param $sandbox * Stores information for multipass updates. See above for more information. * * @throws DrupalUpdateException, PDOException * In case of error, update hooks should throw an instance of DrupalUpdateException * with a meaningful message for the user. If a database query fails for whatever * reason, it will throw a PDOException. * * @return * Optionally update hooks may return a translated string that will be displayed * to the user. If no message is returned, no message will be presented to the * user. * * $COMMENT$ */ function $MODULE_NAME$_update_N(&$sandbox) { // For non-multipass updates, the signature can simply be; // function hook_update_N() {
// For most updates, the following is sufficient. db_add_field('mytable1', 'newcol', array('type' => 'int', 'not null' => TRUE, 'description' => 'My new integer column.'));
// However, for more complex operations that may take a long time, // you may hook into Batch API as in the following example.
// Update 3 users at a time to have an exclamation point after their names. // (They're really happy that we can do batch API in this hook!) if (!isset($sandbox['progress'])) { $sandbox['progress'] = 0; $sandbox['current_uid'] = 0; // We'll -1 to disregard the uid 0... $sandbox['max'] = db_query('SELECT COUNT(DISTINCT uid) FROM {users}')->fetchField() - 1; }
$users = db_select('users', 'u') ->fields('u', array('uid', 'name')) ->condition('uid', $sandbox['current_uid'], '>') ->range(0, 3) ->orderBy('uid', 'ASC') ->execute();
foreach ($users as $user) { $user->name .= '!'; db_update('users') ->fields(array('name' => $user->name)) ->condition('uid', $user->uid) ->execute();
$sandbox['progress']++; $sandbox['current_uid'] = $user->uid; }
$sandbox['#finished'] = empty($sandbox['max']) ? 1 : ($sandbox['progress'] / $sandbox['max']);
// To display a message to the user when the update is completed, return it. // If you do not want to display a completion message, simply return nothing. return t('The update did what it was supposed to do.');
// In case of an error, simply throw an exception with an error message. throw new DrupalUpdateException('Something went wrong; here is what you should do.'); $END$ }

