 * Implements hook_xmlsitemap_sitemap_delete().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_xmlsitemap_sitemap_delete(XmlSitemapInterface $sitemap) {
  db_query("DELETE FROM {mytable} WHERE smid = '%s'", $sitemap->smid);
  $END$
}

