/**
 * Implements hook_language_fallback_candidates_alter().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_language_fallback_candidates_alter(array &$candidates, array $context) {
  $candidates = array_reverse($candidates);
  $END$
}

/**
 * Implements hook_language_fallback_candidates_OPERATION_alter().
 *
 * Allow modules to alter the fallback candidates for specific operations.
 *
 * @param array $candidates
 *   An array of language codes whose order will determine the language fallback
 *   order.
 * @param array $context
 *   A language fallback context.
 *
 * @see \Drupal\Core\Language\LanguageManagerInterface::getFallbackCandidates()
 *
 * $COMMENT$
 */
function $MODULE_NAME$_language_fallback_candidates_OPERATION_alter(array &$candidates, array $context) {
  // We know that the current OPERATION deals with entities so no need to check
  // here.
  if ($context['data']->getEntityTypeId() == 'node') {
    $candidates = array_reverse($candidates);
  }
  $END$
}

/**
 * Implements hook_language_fallback_candidates_OPERATION_alter().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_language_fallback_candidates_OPERATION_alter(array &$candidates, array $context) {
  // We know that the current OPERATION deals with entities so no need to check
  // here.
  if ($context['data']->getEntityTypeId() == 'node') {
    $candidates = array_reverse($candidates);
  }
  $END$
}

/**
 * Implements hook_field_formatter_third_party_settings_form().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_field_formatter_third_party_settings_form(\Drupal\Core\Field\FormatterInterface $plugin, \Drupal\Core\Field\FieldDefinitionInterface $field_definition, $view_mode, $form, \Drupal\Core\Form\FormStateInterface $form_state) {
  $element = array();
  // Add a 'my_setting' checkbox to the settings form for 'foo_formatter' field
  // formatters.
  if ($plugin->getPluginId() == 'foo_formatter') {
    $element['my_setting'] = array(
      '#type' => 'checkbox',
      '#title' => t('My setting'),
      '#default_value' => $plugin->getThirdPartySetting('my_module', 'my_setting'),
    );
  }
  return $element;
  $END$
}

