/**
 * Implements hook_panelizer_pre_view_builder_alter().
 *
 * Allow other modules to alter view data.
 *
 * @param string $view_mode
 *   The view_mode being rendered.
 * @param object $entity
 *   The entity being viewed.
 * @param string $langcode
 *   The langcode.
 *
 * $COMMENT$
 */
function $MODULE_NAME$_panelizer_pre_view_builder_alter(&$view_mode, \Drupal\Core\Entity\EntityInterface $entity, &$langcode) {

