/** * Implements hook_field_info_max_weight(). * * Returns the maximum weight for the entity components handled by the module. * * Field API takes care of fields and 'extra_fields'. This hook is intended for * third-party modules adding other entity components (e.g. field_group). * * @param string $entity_type * The type of entity; e.g. 'node' or 'user'. * @param string $bundle * The bundle name. * @param string $context * The context for which the maximum weight is requested. Either 'form' or * 'display'. * @param string $context_mode * The view or form mode name. * * @return int * The maximum weight of the entity's components, or NULL if no components * were found. * * @ingroup field_info * * $COMMENT$ */ function $MODULE_NAME$_field_info_max_weight($entity_type, $bundle, $context, $context_mode) { $weights = array();
foreach (my_module_entity_additions($entity_type, $bundle, $context, $context_mode) as $addition) { $weights[] = $addition['weight']; }
return $weights ? max($weights) : NULL; $END$ }

/**
 * Implements hook_field_info_max_weight().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_field_info_max_weight($entity_type, $bundle, $context, $context_mode) {
  $weights = array();

  foreach (my_module_entity_additions($entity_type, $bundle, $context, $context_mode) as $addition) {
    $weights[] = $addition['weight'];
  }

  return $weights ? max($weights) : NULL;
  $END$
}

